import {Formik, Form, Field, ErrorMessage} from "formik" ;
import * as yup from "yup";
import './App.css';
import Axios from "axios" ;
import { Component } from "react";

class App extends Component {

  state = {
    login:"",
    password:"",
    confirmpassword:""
  }



  handleClickLogin = (values) => {
    Axios.post("http://localhost:3003/login",{email:values.email, password:values.password}).
    then((response)=>{
      console.log(response);
      if(response.data.status){
        alert("Usario logado, seu registro consta na base de dados");
        values.email = "";
        values.password = "";
      }else{
        alert("Senha ou login invalido");
      }
    })
  };

  handleClickRegister = (values) => {
    Axios.post("http://localhost:3003/register", {email:values.email, password:values.password})
    .then((response) => {
      console.log(response);
      if(response.data.status){
        alert("Usario registrado com sucesso");
        values.email = "";
        values.password = "";
        values.confirmPassword = "";
      }else{
        alert("Usuario ja consta na base de dados");
      }
    })
  };




  render(){

    const {login, password, confirmpassword} = this.state ;

    const validationLogin =  yup.object().shape({
      email: yup.string().email("Email invalido").required(),
      password: yup.string().required().min(8,"A senha deve ter ao menos 8 caracteres"),
    })

    const validationRegister = yup.object().shape({
      email: yup.string().email("Email invalido").required(),
      password: yup.string().required().min(8,"A senha deve ter ao menos 8 caracteres"),
      confirmPassword: yup.string().oneOf([yup.ref("password"),null], "As senhas nao sao iguais")
    })
    return (
      <div className="container">
        <h1>Login</h1>
        <Formik
          initialValues={{}}
          onSubmit ={this.handleClickLogin}
          validationSchema = {validationLogin}
        >
          <Form className="login-form">

            <div className="login-form-group"> 
              <Field name="email" clasName ="form-field" placeHolder="Email"></Field>
              <ErrorMessage component={"span"} name="email" className="form-error"></ErrorMessage>
            </div>

            <div className="login-form-group"> 
              <Field name="password" clasName ="form-field" placeHolder="Senha" type="password"></Field>
              <ErrorMessage component={"span"} name="password" className="form-error"></ErrorMessage>
            </div>

            <button className="button" type="submit">Login</button>

          </Form>

        </Formik>


        <h1>Register</h1>
        <Formik
          initialValues={{}}
          onSubmit ={this.handleClickRegister}
          validationSchema = {validationRegister}
        >
          <Form className="login-form">

            <div className="login-form-group"> 
              <Field name="email" clasName ="form-field" placeHolder="Email">{}</Field>
              <ErrorMessage component={"span"} name="email" className="form-error"></ErrorMessage>
            </div>

            <div className="login-form-group"> 
              <Field name="password" clasName ="form-field" placeHolder="Senha" ></Field>
              <ErrorMessage component={"span"} name="password" className="form-error"></ErrorMessage>
            </div>

            <div className="login-form-group"> 
              <Field name="confirmPassword" clasName ="form-field" placeHolder="Confirme sua Senha" ></Field>
              <ErrorMessage component={"span"} name="confirmPassword" className="form-error"></ErrorMessage>
            </div>

            <button className="button" type="submit">Register</button>

          </Form>

        </Formik>



      </div>
    );
  }

}

export default App;
